								["Id"] = 103212},
						[38] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "师徒几人一行来到高老庄，闻得消息有妖怪入赘其家。半夜常去敲其女儿的门，请他们帮忙除妖。当晚悟空变身妖艳美女前去勾引妖怪，你隐身藏在周边。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 2,["Content"] = "诶？好黑！翠兰，这洞房花烛夜怎么不点蜡烛呢？都看不清你的脸了！"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "老猪你不要靠那么近，看清楚这位是谁？"},
										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 4,["Content"] = "看看我是谁？"},
										[5] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 5,["Content"] = "不是翠兰，这个多毛倒是像猴、猴。。。弼马温？跑！"}
								},
								["Id"] = 104011},
						[39] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 1,["Content"] = "呔，何方妖孽？"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 2,["Content"] = "一个白兔子从草丛中跳出。"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "就一只兔子。。。。。"},
										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 4,["Content"] = "刚说话间，那只白兔子发出一道光线，从光线中出现一名美女。"},
										[5] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 5,["Content"] = "我去！还真是妖精。"},
										[6] = {["Icon"] = "GameScene/ui/headImg/yutu.png",["Type"] = 2,["Name"] = "玉兔",["Id"] = 6,["Content"] = "上仙饶命！我本广寒仙宫中一只玉兔，因贪玩私下凡间，却不慎被那猪头撞见，硬要奴家委身于他，为保清白只能以原形躲入山野之间。"},
										[7] = {["Icon"] = "GameScene/ui/headImg/yutu.png",["Type"] = 2,["Name"] = "玉兔",["Id"] = 7,["Content"] = "今日幸遇上仙，只求上仙收留奴家于身侧，以免遭了那猪头的凌辱。"}
								},
								["Id"] = 104032},
						[40] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 1,["Content"] = "弼马温怎么会在这里？惹不起我还躲不起，跑！"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 2,["Content"] = "肥猪上山了！猴哥我们继续追！"}
