								["Id"] = 104042},
						[41] = {["Talk"] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "孙悟空正要一棍将此猪妖打死，你急忙拦下，并介绍自己一群人准备前往西天取经，猪八戒听到后，即道出自己也是受观音所托护送唐僧的取经人。"},["Id"] = 104082},
						[42] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 1,["Content"] = "这河可真大啊！还有个石碑，流沙河？这流沙河要怎么渡过去？"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 2,["Content"] = "这小河小溪的，我都不用打筋斗就过去啦，上来！被你飞过去。"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "取经是不能取巧的，而且这流沙河可能还没那么容易过去，看！过来了！"}
