						[43] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 1,["Content"] = "这些个虾虾蟹蟹的不够打啊！"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 2,["Content"] = "都是在这条河里出来的，下去看看！"}
								},
								["Id"] = 104102},
						[44] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 1,["Content"] = "看你就这水平也想吃唐僧肉？"},
										[2] = {["Icon"] = "GameScene/ui/headImg/shaseng.png",["Type"] = 2,["Name"] = "沙悟净",["Id"] = 2,["Content"] = "唐僧？取经人？冤枉啊。。。。。。"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 3,["Content"] = "沙悟净道出，原来他也是受观音所托保护唐僧取经而守候在此的。"}
								},
								["Id"] = 104122},
						[45] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "师徒一行来到五庄观，观内道童在门外迎接，原来是镇元大仙知道取经人从此路过，便想招待取经人尽下地主之谊，但因有事外出便先叫座下道童先引入观内。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 2,["Content"] = "来到五庄观没理由不尝尝人参果的，可是那些个牛气冲天的小道童又不会给我们，看来还是要猴哥出马。"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "猴哥听闻这道观种了棵人参果树，树上结的人参果比天庭的蟠桃还要有滋味。"},
										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 4,["Content"] = "还有这事？要不搞几个师兄弟一起尝尝？不过这些小道童到处走不好搞。"},
										[5] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 5,["Content"] = "我来引开他们，你去搞点来。不过要注意啊，人参果可以，树你可不要搞坏了啊，世上就这一棵的，搞坏以后没得吃了。"},
										[6] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 6,["Content"] = "小道童！今天天气晴朗，微风送爽。正是比试斗法的好日子，来！看招！"}
								},
								["Id"] = 105011},
						[46] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/qingfeng.png",["Type"] = 2,["Name"] = "清风",["Id"] = 1,["Content"] = "一、二、三......不好。师兄，人参果少了几个！"},
										[2] = {["Icon"] = "GameScene/ui/headImg/mingyue.png",["Type"] = 2,["Name"] = "明月",["Id"] = 2,["Content"] = "肯定是那些取经人偷了，不要让他们跑了！"}
