								["Id"] = 105031},
						[47] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 1,["Content"] = "还没吃上呢，那些小道童就发现了，猴哥你手生了啊。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 2,["Content"] = "没办法这个人参果跟蟠桃还不太一样，麻烦很多，不好摘。"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "好像那边的老大也回来了，完了跑不了，早知道不做死了。"}
								},
								["Id"] = 105041},
						[48] = {["Talk"] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "与镇元大仙打了一架，之后镇元大仙得知你们是取经一行，不仅原谅你们偷果的行为，还设宴款待你等。"},["Id"] = 105062},
						[49] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "师徒一行行至白虎岭，众人感觉饥饿，就让孙悟空去找些吃点。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/baigujing.png",["Type"] = 2,["Name"] = "村姑",["Id"] = 2,["Content"] = "众位师父，我本来是给我丈夫送饭的，见众位师父在此。不知是否用过膳，如果不介意，我这还有几份斋饭。"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 3,["Content"] = "八戒正要接村姑的斋饭，你连忙制止并告知这个村姑是妖精。"},
										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "八戒",["Id"] = 4,["Content"] = "那有那么多妖精，明明是心地善良的女菩萨。（说着便接过了斋饭，刚接过手，手中斋饭变为几个青蛙、癞虾蟆，满地乱跳。）"},
										[5] = {["Icon"] = "GameScene/ui/headImg/baigujing.png",["Type"] = 2,["Name"] = "白骨精",["Id"] = 5,["Content"] = "没想到走了个弼马温，还有另一个弼马温。眼神还可以，就看看你身手怎样。"}
								},
								["Id"] = 105071},
						[50] = {["Talk"] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "与白骨精激战数回合，白骨精不敌。使用尸解法遁去，留下地上的尸体，你见状便使用法术将其留下的尸体藏了起来。"},["Id"] = 105082},
						[51] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "山坡上闪出一个年满八旬的老妇人，手拄着弯头竹杖，一步一声地哭着走来。最近的沙僧连忙上次搀扶。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/baigujing.png",["Type"] = 2,["Name"] = "老妪",["Id"] = 2,["Content"] = "可怜我的好女儿啊，就这样没了啊！"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "什么女儿，这荒郊野岭的。"},
										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 4,["Content"] = "老妪看了一眼众人身后发现地上竟找不到尸体。"},
										[5] = {["Icon"] = "GameScene/ui/headImg/baigujing.png",["Type"] = 2,["Name"] = "老妪",["Id"] = 5,["Content"] = "怎么会......"},
										[6] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 6,["Content"] = "还要装？"}
								},
								["Id"] = 105091},
						[52] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "旁白：不远处又过来一个白发苍髯的老翁，数珠掐在手，口诵南无经。你见了，正要举起武器击杀妖精。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/tangsen.png",["Type"] = 2,["Name"] = "唐僧",["Id"] = 2,["Content"] = "唐僧：等等，先问个清楚啊，万一不是妖精呢？不能乱造杀孽。"},
										[3] = {["Icon"] = "GameScene/ui/headImg/baigujing.png",["Type"] = 2,["Name"] = "老翁",["Id"] = 3,["Content"] = "老翁：我老汉祖居此地，只生得一个小女，招了个女婿，今早送饭下田，想是遭逢虎口。老妻先来找寻，也不见回去，全然不知下落，老汉特来寻看。果然是伤残他命，也没奈何，将她骸骨收拾回去，安葬茔中。"},
										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 4,["Content"] = "你：哦？看你逢此祸事，仍手掐佛珠，口诵佛经。大抵也是一心向佛的人，不如口诵全篇地藏菩萨本愿经超度一下亲人？"},
										[5] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 5,["Content"] = "旁白：老翁乃白骨精所化，佛经只通数段而已，哪里能背诵长篇而艰涩难懂的地藏菩萨本愿经？白骨精自知已被识破，便现出原形，招出大量骷髅士兵，包围住师徒一行。"}
								},
								["Id"] = 105111},
						[53] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "解决了白骨精后，行至碗子山。你出来采摘野果，留下孙悟空等三人保护唐僧。但孙悟空无法静候，过了一会便留下八戒与沙僧保护师傅，自顾自飞走了。你采摘玩野果后回到原地……"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 2,["Content"] = "你两个在干嘛？怎么只有你们两个？"},
										[3] = {["Icon"] = "GameScene/ui/headImg/shaseng.png",["Type"] = 2,["Name"] = "沙悟净",["Id"] = 3,["Content"] = "（双眼含泪跑过来）你走了不久，猴哥就耐不住，打了个筋斗就不知道去哪了。然后就刚才白龙马脚下有条蜈蚣，吓得白龙马跑起来，一跑起来就带着师父掉那悬崖下面了，可怜的师父啊！"},
										[4] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 4,["Content"] = "我看师父也是完了，我们还是先把行李分了，速度各回各家算了，晚点等那猴头回来就要分多一份了！"},
										[5] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 5,["Content"] = "我靠，这个白龙马是不是忘了自己曾经还是条龙？还有你们两个一个天蓬元帅，一个卷帘大将，敢先飞下去看看师父怎么样？"}
								},
								["Id"] = 106011},
						[54] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 1,["Content"] = "这下面妖精可真多？师父目测不摔死都要被妖精分去煮了吃了。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 2,["Content"] = "狼妖？碗子山？师父可能有救，快点走！"}
								},
								["Id"] = 106021},
						[55] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 1,["Content"] = "别打了，奎木狼！放了我师父，我帮你圆了你与百花羞的缘分。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/huangpaoguai.png",["Type"] = 2,["Name"] = "黄袍怪",["Id"] = 2,["Content"] = "你知道我是谁？你还知道百花羞在哪？"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "我能告诉你百花羞在哪，我也能让你们回到天庭。我和猴哥在天庭还是有几分薄面的，毕竟朝中有人好办事，你跟百花羞那点是小事。"},
										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 4,["Content"] = "实在不行可以再闹一次天宫嘛！"},
										[5] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 5,["Content"] = "你帮黄袍怪找回百花羞，你与猴哥也无需再次大闹天宫就帮他们解决了天庭结婚难的问题，众徒弟也接回完好无损的唐僧。"}
								},
								["Id"] = 106042},
						[56] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 1,["Content"] = "这里就是宝象国皇宫了，百花羞下凡转生成了宝象国的公主。你们先在这里等等别吓着人了，我先去探探风！"},
										[2] = {["Icon"] = "GameScene/ui/headImg/tianbing.png",["Type"] = 2,["Name"] = "卫兵",["Id"] = 2,["Content"] = "一队卫兵路过，妖怪啊！妖怪闯进皇宫啦！！！"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 3,["Content"] = "这么麻烦，直接打进去算了！"},
										[4] = {["Icon"] = "GameScene/ui/headImg/huangpaoguai.png",["Type"] = 2,["Name"] = "黄袍怪",["Id"] = 4,["Content"] = "对！直接进去吧！"}
								},
								["Id"] = 106051},
						[57] = {["Talk"] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "虽然过程不太愉快，不过还是成功让黄袍怪与百花羞成功见面，同时天庭因你们的压力而也让黄袍怪与百花羞返回天庭！"},["Id"] = 106062},
						[58] = {["Talk"] = {
