										[2] = {["Icon"] = "GameScene/ui/headImg/xiabing.png",["Type"] = 2,["Name"] = "虾兵",["Id"] = 2,["Content"] = "来人止步，我们受井龙王所托守护此地，闲杂人等速速离开！"},
