						[62] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiaguowang.png",["Type"] = 2,["Name"] = "乌鸡国国王鬼魂",["Id"] = 1,["Content"] = "感谢各位上仙助我脱困，我本是这个乌鸡国的国王，五年前，有一能呼风唤雨、点石成金的道士全真来到这里，我与他结为兄弟。可是没想到，三年前，将我推下井，浸了三年，并化身为我的外表代替我做了乌鸡国国王。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 2,["Content"] = "这些个牛鼻子道士真是不知道好歹，心肠狠毒。走，今天我就帮你讨个公道！"},
