						[63] = {["Talk"] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "原来他好善斋僧，如来佛祖差文殊菩萨度他归西，早证金身罗汉。因是不可原身相见，文殊菩萨变做一种凡僧，问国王化些斋供。不料乌鸡国国王被文殊菩萨几句言语相难，将其一条绳捆了，送在那御水河中，浸了三日三夜。多亏六甲金身救文殊归西，奏与如来，如来将狮猁王令到乌鸡国，将乌鸡国国王浸够三年等待取经人来此将其救出。"},["Id"] = 107062},
						[64] = {["Talk"] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "师徒一行人路过号山枯松涧，突然唐僧被一股怪风卷走，主角与悟空赶忙去追。。。"},["Id"] = 107071},
