										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 2,["Content"] = "真的假的？这么神奇？等一下，这个怎么看着这么眼熟？"},
