						[69] = {["Talk"] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "红孩儿被你们制服，唐僧已救出，正当你们正烦恼怎么处置红孩儿时，菩萨出现收服红孩儿为座下善财童子。"},["Id"] = 107142},
						[70] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "虎力、鹿力、羊力来到车迟国后被国王尊为国师，在全国独尊道教，把众佛教徒驱服道家做苦役。佛教徒大多被虐死。唐僧师徒西行到达车迟国，见五百佛教徒被道众暴力驱使，鬼哭狼嚎。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 2,["Content"] = "师傅，到车迟国了，我们找个地方休息一下吧。"},
										[3] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 3,["Content"] = "猴哥，你看哪，怎么回事啊，那群和尚怎么给锁起来当苦力了？"},
										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 4,["Content"] = "听闻车迟国内兴道灭佛，和尚们都给抓起来了。"},
										[5] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "悟空",["Id"] = 5,["Content"] = "凭什么捉起来，救了人先，再问问清楚。"}
								},
								["Id"] = 108011},
						[71] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "悟空夜中饥饿，于是约你等到三清殿偷吃贡品。一小道见贡品消失以为三清显灵报知国师，国师引众进殿，向三清拜求仙丹。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 2,["Content"] = "要被发现了，要不闪人？"},
										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "等等，人家在求仙丹呢！仙丹没有了，仙泉应该有，猴哥刚才不是说有点尿急么？"},
