						[72] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "三大仙向国王奏说，悟空等人打死道士，要求处死众人。国王因惧怕大唐不允，又因看到悟空等人的手段厉害，却只敢拖着通关文书，使师徒一行也无法离开。"},
										[2] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "孙悟空",["Id"] = 2,["Content"] = "国王给我出来！为什么还不给我们通关文书，让我们等了那么好几天？"},
