										[4] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 2,["Name"] = "鹿力大仙",["Id"] = 4,["Content"] = "对！通关文书没有！回去等着吧。"},
										[5] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 5,["Content"] = "捉和尚做苦力的事还没跟你算账，现在还这样拖我们？咱们都是修仙之人，上次见面国王有没有病看不出来？不就是请了一顿仙泉给你们么。"},
										[6] = {["Icon"] = "GameScene/ui/headImg/huli.png",["Type"] = 2,["Name"] = "虎力",["Id"] = 6,["Content"] = "果然是你们，今天你们就不用走了！"}
								},
								["Id"] = 108051},
						[73] = {["Talk"] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "战败后，三大仙现出原形原来分别为虎、鹿、羊。车迟国惊闻三大仙是妖精所变，连忙出去与师徒一行道歉，并释放那些僧侣，给予师徒一行通关文书放行。"},["Id"] = 108142},
						[74] = {["Talk"] = {
										[1] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 3,["Name"] = "旁白",["Id"] = 1,["Content"] = "路过通天河，于通天河边借宿一老者家中。得知河里有专吃童男童女的妖怪，决心除掉他。于是孙悟空和猪八戒假扮童男童女，而你埋伏一旁，等待妖怪上门。"},
