										[3] = {["Icon"] = "GameScene/ui/headImg/jiahouwang.png",["Type"] = 1,["Name"] = "你",["Id"] = 3,["Content"] = "。。老猪，你想说什么？"},
										[4] = {["Icon"] = "GameScene/ui/headImg/bajie.png",["Type"] = 2,["Name"] = "猪八戒",["Id"] = 4,["Content"] = "把我留下多好啊，师父累了还可以当个靠枕什么的。"},
